import { createContext, useState } from "react";

interface userState {
    user: string,
    pass: string,
}

interface userActionsState {
    id?: number,
    simbolo: string,
    nombre: string,
    moneda: string,
}

const initialState = {
    user: {
        user: 'pepito',
        pass: 'pepito',
    },
    actions: {
        id: 0,
        simbolo: '',
        nombre: '',
        moneda: '',
    }
}

export interface userStateProps {
    user: userState,
    actions: userActionsState,
    setUsuario: ({user,pass}: userState) => void,
    setAcciones: ({simbolo, nombre, moneda}: userActionsState) => void
}

export const userContext = createContext({} as userStateProps);

export const UserContextProvider = ({children} :any) => {

    const [user, setUser] = useState<userState>(initialState.user);
    const [actions, setActions] = useState<userActionsState>(initialState.actions);

    const setUsuario = ({user, pass}: userState)  => {
        setUser({user, pass})
    }

    const setAcciones = ({simbolo, nombre, moneda}: userActionsState)  => {
        setActions({simbolo, nombre, moneda})
    }

    return (
        <userContext.Provider
            value={{user, actions, setUsuario, setAcciones}}
        >
            {children}
        </userContext.Provider>
    )
}
