import { useState, useEffect, useContext } from 'react';
import twelvedata from '../api/twelvedataAPI';
import users from '../api/users';
import { Actions, Result } from '../interfaces/twelvedataAPI';
import { userContext } from '../context/UserContext';

const useGetActions = () => {

    const [actions, setActions] = useState<Actions[]>([]);
    const [isLoading, setIsLoading] = useState(true)
    const { user } = useContext(userContext);
    const [actionsUser, setActionsUser] = useState<Actions[]>([]);

    const loadActions = async () => {
        try {
            const res = await twelvedata.get<Result>('https://api.twelvedata.com/stocks?source=docs&exchange=BMV');
            setActions(res.data.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error);
        }
    }

    const loadActionsUser = async () => {
        try {
            const res = await users.post('/getActionsUser',{ user: user.id })
            console.log(res);
            setActionsUser(res.data)
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        loadActions();
        loadActionsUser();
    }, [])

    return {
        actions,
        actionsUser,
        isLoading,
        loadActionsUser
    }
}

export default useGetActions
