import { useState, useEffect } from 'react';
import useAuth from '../hooks/useAuth';
import { useHistory } from 'react-router-dom';
import twelvedata, {apikey} from '../api/twelvedataAPI';

import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";


const ActionDetail = ({match}: any) => {

    const { user, isLogged } = useAuth();
    const [actionSelected, setActionSelected] = useState(JSON.parse(match.params.action))
    const history = useHistory();
    const [optionSelected, setOptionSelected] = useState(0)
    const [intervalo, setIntervalo] = useState(1)
    const [chartOptions, setChartOptions] = useState({series: [{data:[], name:'',enablePolling:true,dataRefreshRate:10 }], title: {text:''}});

    useEffect(() => {
        if(isLogged === false)
            history.push('/')

    }, [isLogged, history])

    const graficar = async () => {
        if(optionSelected === 0){
            const res = await twelvedata.get(`https://api.twelvedata.com/time_series?symbol=${actionSelected.symbol}&interval=${intervalo}min&apikey=${apikey}`)
            setChartOptions({
                series: [
                    {
                        data: res.data.values.map(({open}: any) => +open),
                        name:'Cotización',
                        enablePolling: true,
                        dataRefreshRate: intervalo
                    }
                ],
                title: {
                    text:actionSelected.symbol
                },
            })
        }

        setInterval(await graficar, 500);
    }

    const changeInterval = (val: string) => {
        setIntervalo(+val)
    }

    return (
        <div className='container'>
            <div className='header'>
                <p>{actionSelected.symbol} - {actionSelected.name} - {actionSelected.currency}</p>
                <p>Usuario: {user?.login}</p>
            </div>
            <div>
                <input type="radio" name="graficar" id="graficar" checked={optionSelected === 0? true : false} />
                &nbsp;&nbsp;Tiempo Real
            </div>
            <div style={{marginTop: 50, display:'flex', width:500, justifyContent: 'flex-start'}}>
                &nbsp;&nbsp;Intervalo&nbsp;&nbsp;&nbsp;&nbsp;
                <select onChange={(e) => changeInterval(e.target.value)} name="intervalo" id="intervalo">
                    <option value="1">1 Minuto</option>
                    <option value="5">5 Minutos</option>
                    <option value="15">15 Minutos</option>
                </select>
            </div>
            <div id='grafico' style={{marginTop: 50, display:'flex', width:500, justifyContent: 'flex-end'}}>
                <button onClick={graficar} className='btn btn-primary btn-sm'>Graficar</button>
            </div>
            <div style={{marginTop: 50, display:'flex', width:800, justifyContent: 'flex-start'}}>
                {
                    chartOptions?.series[0]?.data?.length ? <HighchartsReact highcharts={Highcharts} options={chartOptions} /> : ''
                }
            </div>
        </div>
    )
}

export default ActionDetail
