import { useState, useEffect, useRef, useCallback } from 'react';
import useAuth from '../hooks/useAuth';
import { useHistory, useLocation } from 'react-router-dom';
import twelvedata, {apikey} from '../api/twelvedataAPI';

import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";


const ActionDetail = ({match}: any) => {

    const location = useLocation()
    const { user, isLogged } = useAuth();
    const actionSelected = useRef({action: {symbol:''}}).current
    const history = useHistory();
    const [optionSelected, setOptionSelected] = useState(0)
    const [intervalo, setIntervalo] = useState(1)
    const [chartOptions, setChartOptions] = useState<any>();
    const [values, setValues] = useState<any>([])
    const [desde, setDesde] = useState('')
    const [hasta, setHasta] = useState('')
    const [error, setError] = useState('')
    const [timeInterval, setTimeInterval] = useState<any>()

    console.log(location);

    useEffect(() => {
        if(isLogged === false)
            history.push('/')

    }, [isLogged, history])

    const changeOptions = useCallback(() => {
            setChartOptions({
                series: [
                    {
                        data: values.map(({value}: any) => value),
                        name:'CotizaciÃ³n',
                        enablePolling: true,
                        dataRefreshRate: intervalo
                    },
                ],
                title: {
                    text:actionSelected.symbol
                },
                xAxis: [{
                    tickintervalo: 1,
                    title: {
                        text: 'intervalo',
                    },
                    labels: {
                            enabled: true,
                            formatter: (val: any) => values[val.value].date
                        }
                }],
                yAxis: [{
                    title: {
                        text: 'Cotizacion'
                    }
                }],
            })
        },[intervalo, actionSelected.symbol, values])

    useEffect(() => {
        changeOptions()
    }, [values, changeOptions])

    const graficar = async () => {
        const time = setInterval(() => loadValues(),intervalo * 60000)
        setTimeInterval(time)
    }

    const loadValues = async () => {

        clearInterval(timeInterval)
        setError('')
        let newValues = [];

        if(optionSelected === 0){
            try {
                const res = await twelvedata.get(`https://api.twelvedata.com/time_series?symbol=${actionSelected.symbol}&interval=${intervalo}min&apikey=${apikey}`)
                newValues = res.data.values.map(({datetime,open}: any) => {
                    const d = new Date(datetime)
                    const date = d.getHours()+':'+(d.getMinutes()<10?'0':'') + d.getMinutes();
                    return {date, value: +open }
                })
                setValues(newValues.reverse().slice(newValues.length - 5,newValues.length))
                graficar()
            } catch (error) {
                console.log(`https://api.twelvedata.com/time_series?symbol=${actionSelected.symbol}&interval=${intervalo}min&apikey=${apikey}`);
                console.log(error);
            }
        }

        if(optionSelected === 1){

            if(desde.trim() === '' || hasta.trim() === '') {
                setError('Por favor seleccione una fecha de inicio y de fin')
                return false;
            }

            const date_desde = new Date(desde);
            const date_hasta = new Date(hasta);

            if(date_desde > date_hasta){
                setError('Por favor seleccione una fecha de inicio menor a la de fin')
                return false;
            }

            try {
                const res = await twelvedata.get(`https://api.twelvedata.com/time_series?symbol=${actionSelected.symbol}&interval=${intervalo}min&start_date=${desde}%2009:48:00&end_date=${hasta}%2019:48:00&apikey=${apikey}`)
                newValues = res.data.values.map(({datetime,open}: any) => {
                    const d = new Date(datetime)
                    const date = d.getHours()+':'+(d.getMinutes()<10?'0':'') + d.getMinutes();
                    return {date, value: +open }
                })
                setValues(newValues.reverse().slice(newValues.length - 5,newValues.length))
                graficar()
            } catch (error) {
                console.log(`https://api.twelvedata.com/time_series?symbol=${actionSelected.symbol}&interval=${intervalo}min&start_date=${desde}%2009:48:00&end_date=${hasta}%2019:48:00&apikey=${apikey}`);
                console.log(error);
            }
        }

    }

    const changeinterval = (val: string) => {
        setIntervalo(+val)
    }

    return (
        <div className='container'>
            <div className='header'>
                <p>{actionSelected.symbol} - {actionSelected.name} - {actionSelected.currency}</p>
                <p>Usuario: {user?.login}</p>
            </div>
            <div>
                <input type="radio" name="graficar" onChange={()=> setOptionSelected(0)} id="graficar" checked={optionSelected === 0? true : false} />
                &nbsp;&nbsp;Tiempo Real
                <br></br><br></br>
                <input type="radio" name="graficar" onChange={()=> setOptionSelected(1)} id="graficar" checked={optionSelected === 1? true : false} />
                &nbsp;&nbsp;HistÃ³rico

                &nbsp;&nbsp;
                Desde <input type='date' onChange={(e)=>setDesde(e.target.value)} placeholder='Desde' />
                &nbsp;&nbsp;&nbsp;&nbsp;
                Hasta <input type='date' onChange={(e)=>setHasta(e.target.value)} placeholder='Hasta' />

                {error && <p style={{marginTop: 20}} className='text-danger'>{error}</p>}

            </div>
            <div style={{marginTop: 50, display:'flex', width:500, justifyContent: 'flex-start'}}>
                &nbsp;&nbsp;Intervalo&nbsp;&nbsp;&nbsp;&nbsp;
                <select onChange={(e) => changeinterval(e.target.value)} name="intervaloo" id="intervaloo">
                    <option value="1">1 Minuto</option>
                    <option value="5">5 Minutos</option>
                    <option value="15">15 Minutos</option>
                </select>
            </div>
            <div id='grafico' style={{marginTop: 50, display:'flex', width:500, justifyContent: 'space-between'}}>
                <button onClick={() => history.goBack()} className='btn btn-warning btn-sm'>Volver</button>
                <button onClick={loadValues} className='btn btn-primary btn-sm'>Graficar</button>
            </div>
            <div style={{marginTop: 50, display:'flex', width:800, justifyContent: 'flex-start'}}>
                {
                    values.length ? <HighchartsReact highcharts={Highcharts} options={chartOptions} /> : ''
                }
            </div>
        </div>
    )
}

export default ActionDetail
