import { useContext, useState, useEffect } from 'react';
import { userContext } from '../context/UserContext';
import useDebauncedValue from '../hooks/useDebauncedValue';
import '../styles/MisAcciones.css'
import useGetActions from '../hooks/useGetActions';
import { Actions } from '../interfaces/twelvedataAPI';
import Spinner from './Spinner';

const MisAcciones = () => {

    const [search, setSearch] = useState('');
    const { user } = useContext(userContext);
    const debauncedValue = useDebauncedValue(search);
    const { actions, isLoading } = useGetActions();
    const [filteredValues, setFilteredValues] = useState<Actions[]>([]);
    const [searching, setSearching] = useState(false)
    const [selectedAction, setSelectedAction] = useState<Actions>()

    useEffect(() => {
        if( debauncedValue){
            const newValues = actions.filter(act => act.name.toLowerCase().includes(debauncedValue.toLowerCase()))
            setFilteredValues(newValues)
            setSearching(false)
        } else{
            setFilteredValues([])
        }
    }, [debauncedValue])

    useEffect(() => {
        if(search)
            setSearching(true)
    }, [search])

    const selectAction = (e: any, action: Actions) => {
        setSelectedAction(action)
    }
    
    console.log(selectedAction);

    return (
        <div className='container'>
            <div className='header'>
                <p>Mis acciones</p>
                <p>Usuario: {user.name}</p>
            </div>
            <div className='content'>
                <div className='containerAutocomplete'>
                    <p style={{marginBottom: 0}} >Simbolo</p>
                    <div className='contentList'>
                        <input type='text' disabled={isLoading ? true : false} value={selectedAction ? selectedAction.name : search} onChange={(e: React.ChangeEvent<{ value: string }>) => setSearch(e.target.value)} className='form-control' />
                        {
                            filteredValues.length ?
                                <ul style={{position:'absolute', marginTop: '38px', width: '300px'}} className='list-group'>
                                    {
                                        filteredValues.map(act => <li onClick={(e) => selectAction(e, act)} key={act.name + act.symbol} style={{cursor:'pointer'}} className='list-group-item'>{act.name}</li>)
                                    }
                                </ul> : ''
                        }
                    </div>
                </div>
                {
                    searching && <Spinner/>
                }
                <button className='btn btn-primary btn-sm simbolo'>Agregar Simbolo</button>
            </div>
            <div className='table-actions'>
                <table className='table table-condensed'>
                    <thead>
                        <tr>
                            <th>Simbolo</th>
                            <th>Nombre</th>
                            <th>Moneda</th>
                            <th></th>
                        </tr>
                    </thead>
                </table>
            </div>
        </div>

    )
}

export default MisAcciones
